-- =============================================
-- 修復 skills 表：182 版 50 個魔法 + NPC/精靈魔法
-- ID 使用 JP 標準：(skill_level-1)*8 + skill_number + 1
-- 
-- 公式：JP_ID = (skill_level - 1) * 8 + skill_number + 1
-- 例：Lv2 No0 (解毒術) → (2-1)*8 + 0 + 1 = 9
-- =============================================

-- ============================================
-- 第一步：修復 character_skills 表中的舊 182 ID
-- 必須在 TRUNCATE skills 之前執行
-- 使用 CASE/WHEN 原子更新，避免級聯衝突
-- （例：182 ID 9→JP 12，但 182 ID 12 也存在）
-- ============================================
UPDATE character_skills
SET skill_id = CASE skill_id
    -- Lv2: 182 ID 6-10 → JP ID 9-13
    WHEN 6  THEN 9
    WHEN 7  THEN 10
    WHEN 8  THEN 11
    WHEN 9  THEN 12
    WHEN 10 THEN 13
    -- Lv3: 182 ID 11-15 → JP ID 17-21
    WHEN 11 THEN 17
    WHEN 12 THEN 18
    WHEN 13 THEN 19
    WHEN 14 THEN 20
    WHEN 15 THEN 21
    -- Lv4: 182 ID 16-20 → JP ID 25-29
    WHEN 16 THEN 25
    WHEN 17 THEN 26
    WHEN 18 THEN 27
    WHEN 19 THEN 28
    WHEN 20 THEN 29
    -- Lv5: 182 ID 21-25 → JP ID 33-37
    WHEN 21 THEN 33
    WHEN 22 THEN 34
    WHEN 23 THEN 35
    WHEN 24 THEN 36
    WHEN 25 THEN 37
    -- Lv6: 182 ID 26-30 → JP ID 41-45
    WHEN 26 THEN 41
    WHEN 27 THEN 42
    WHEN 28 THEN 43
    WHEN 29 THEN 44
    WHEN 30 THEN 45
    -- Lv7: 182 ID 31-35 → JP ID 49-53
    WHEN 31 THEN 49
    WHEN 32 THEN 50
    WHEN 33 THEN 51
    WHEN 34 THEN 52
    WHEN 35 THEN 53
    -- Lv8: 182 ID 36-40 → JP ID 57-61
    WHEN 36 THEN 57
    WHEN 37 THEN 58
    WHEN 38 THEN 59
    WHEN 39 THEN 60
    WHEN 40 THEN 61
    -- Lv9: 182 ID 41-45 → JP ID 65-69
    WHEN 41 THEN 65
    WHEN 42 THEN 66
    WHEN 43 THEN 67
    WHEN 44 THEN 68
    WHEN 45 THEN 69
    -- Lv10: 182 ID 46-50 → JP ID 73-77
    WHEN 46 THEN 73
    WHEN 47 THEN 74
    WHEN 48 THEN 75
    WHEN 49 THEN 76
    WHEN 50 THEN 77
    ELSE skill_id
END
WHERE skill_id BETWEEN 6 AND 50;

-- ============================================
-- 第二步：清空並重建 skills 表
-- ============================================
TRUNCATE TABLE skills;

